<script>
    document.addEventListener("DOMContentLoaded", function() {
        const paths = document.querySelectorAll("path"); // Ambil semua elemen path
        const texts = document.querySelectorAll("text"); // Ambil semua teks

        texts.forEach(text => {
            let x = text.getAttribute("x");
            let y = text.getAttribute("y");

            // Cari path terdekat dengan koordinat teks
            let closestPath = null;
            let minDistance = Infinity;

            paths.forEach(path => {
                let bbox = path.getBBox(); // Ambil bounding box dari path

                // Cek apakah teks ada di dalam path
                if (x >= bbox.x && x <= bbox.x + bbox.width && y >= bbox.y && y <= bbox.y + bbox
                    .height) {
                    let distance = Math.abs(bbox.x - x) + Math.abs(bbox.y - y);
                    if (distance < minDistance) {
                        minDistance = distance;
                        closestPath = path;
                    }
                }
            });

            if (closestPath) {
                let bgColor = window.getComputedStyle(closestPath).getPropertyValue(
                    "fill"); // Ambil warna fill path

                // Jika warna hijau, teks jadi hitam, jika selain itu jadi putih
                if (bgColor === "rgb(112, 188, 129)") {
                    text.setAttribute("fill", "black");
                } else {
                    text.setAttribute("fill", "white");
                }
            }
        });
    });
</script>


<script>
    document.querySelectorAll("[id^='homepeta'] path").forEach(path => {
        path.addEventListener("click", function() {
            let suhu = this.getAttribute("data-suhu") || "Tidak tersedia";
            let kelembapan = this.getAttribute("data-kelembapan") || "Tidak tersedia";
            let hujan = this.getAttribute("data-hujan") || "Tidak tersedia";
            let hama = this.getAttribute("data-hama") || "Tidak tersedia";
            let kecamatan = this.getAttribute("data-kecamatan") || "Tidak tersedia";

            // Masukkan data ke dalam modal
            document.getElementById("modalSuhu").innerText = suhu;
            document.getElementById("modalKelembapan").innerText = kelembapan;
            document.getElementById("modalHujan").innerText = hujan;
            document.getElementById("modalHama").innerText = hama;
            document.getElementById("modalKecamatan").innerText = kecamatan;

            // Hapus backdrop sebelumnya
            document.querySelectorAll(".modal-backdrop").forEach(backdrop => backdrop.remove());

            // Tampilkan modal Bootstrap
            let myModalEl = document.getElementById("infoModal");
            let myModal = new bootstrap.Modal(myModalEl);
            myModal.show();
        });
    });
</script>
